
drop  table if exists sys_cw_km ;
create table sys_cw_km(
                        id bigint primary key auto_increment,
                        bm varchar(50) not null,
                        name varchar(100) not null,
                        c_kind varchar(20) not null,
                        c_use varchar(100) ,
                        fid bigint default 0
) ;
select * from sys_cw_km ;


select * from sys_cw_km ;
drop table if exists cw_km ;
create table cw_km (
  select * from sys_cw_km
)
;
select * from cw_km ;
alter table cw_km change id id bigint primary key auto_increment ;
create index idx_cw_km_fid on cw_km (fid) ;
create index idx_cw_km_kind on cw_km(c_kind) ;

select count(c_kind) as cnt,c_kind from cw_km group by c_kind order by cnt desc ;




